import React from 'react';
import Sketch from 'react-p5';
import ColorSelector from './ColorSelector';
import FileInput from '../FileInput';

var penState = 0;
var ellipseSize = 10;
var tightness = 20;

var initial;
var x;
var y;
var px;
var py;
var path = [];
var currentPath = [];
var previousPath = [];
var img;
var pg;

var weight;


export default function P5Lasso() {
  const [color, setColor] = React.useState(['#ff0000']);
  const [image, setImage] = React.useState(null);

  function setup(p5, canvasParentRef) {
    p5.createCanvas(600, 600).parent(canvasParentRef);
    pg = p5.createGraphics(600, 600);
    img = p5.loadImage(image, img => {
      p5.image(img, 0, 0);
    });
  }

  function draw(p5) {

    x = p5.mouseX;
    y = p5.mouseY;
    px = p5.mouseX;
    py = p5.mouseY;

    p5.image(img, 0, 0);
    pg.strokeWeight(15);

    if (p5.mouseIsPressed) {

      console.log(color);
      pg.stroke(color);


      for (var i = 1; i < path.length; i++) {
        path = [x, y];
        currentPath = path[i];
        previousPath = path[i - 1];

        var distance = p5.dist(currentPath, previousPath);

        var extraPoints = pg.round(distance / ellipseSize * tightness);
        pg.ellipse(px, py, ellipseSize, ellipseSize);

        for (var j = 0; j < extraPoints; j++) {
          var interpolation = pg.map(j, 0, extraPoints, 0.0, 1.0);
          var inbetween = pg.Vector.lerp(previousPath, currentPath, interpolation);
          pg.ellipse(inbetween.x, inbetween.y, ellipseSize, ellipseSize);
        }
      }

      path.push(Vector(x, y))
      initial = path[0];
      console.log(initial);
      p5.image(pg, 0, 0);
    }
  }


  //function mouseReleased(p5) {
  //if (image) {
  //pg.line(x, y, ...initial);
  //currentPath = [];
  //path.push(currentPath);
  //}
  //}

  if (image) {
    return (

      <div>
        {image && (
          <div>
            <Sketch setup={setup} draw={draw}
            //mouseReleased={mouseReleased}
            />
          </div>
        )}
        <ColorSelector selectColor={color => setColor(color)} />
      </div>
    )
  } else {
    return (
      <FileInput selectImage={setImage} />
    );
  }
}
