import React from 'react';
import Sketch from 'react-p5';
import ColorSelector from './ColorSelector';
import FileInput from '../FileInput';

var penState = 0;
var ellipseSize = 10;
var tightness = 6;

var initial_point;
var x;
var y;
var px;
var py;
var path = [];
var currentPath = [];
var img;
var pg;

var weight;


export default function P5Lasso() {
  const [color, setColor] = React.useState(['#ff0000']);
  const [image, setImage] = React.useState(null);

  function setup(p5, canvasParentRef) {
    p5.createCanvas(600, 600).parent(canvasParentRef);
    pg = p5.createGraphics(600, 600);
    pg.framerate(10);
    img = p5.loadImage(image, img => {
      p5.image(img, 0, 0);
    });
  }

  function draw(p5) {

    x = p5.mouseX;
    y = p5.mouseY;
    px = p5.mouseX;
    py = p5.mouseY;

    p5.image(img, 0, 0);
    pg.strokeWeight(15);

    for (var i = 1; i < path.length; i++) {
      var current = path[i];
      var previous = path[i - 1];

      var distance = previous.dist(current);
      //maybe use lerp for instead

      var extraPoints = round(distance / ellipseSize * tightness);
      ellipse(px, py, ellipseSize, ellipseSize);

      for (var j = 0; j < extraPoints; j++) {

        //work out a normalized (between 0.0 and 1.0) value of where each extra point should be
        //think of this as a percentage along a line: 0.0 = start of line, 0.5 = 50% along the line, 1.0 = end of the line
        var interpolation = map(j, 0, extraPoints, 0.0, 1.0);

        //compute the point in between using PVector's linear interpolation (lerp()) functionality
        var inbetween = p5.Vector.lerp(previous, current, interpolation);

        //draw the point in between
        ellipse(inbetween.x, inbetween.y, ellipseSize, ellipseSize);
      }
    }

    if (p5.mouseIsPressed === true) {

      console.log(color);
      pg.stroke(color);

      path.push(createVector(x, y))
      initial_point = currentPath[0];
      console.log(initial_point);
    }
    p5.image(pg, 0, 0);
  }

  function mouseReleased(p5) {
    if (image) {
      pg.line(x, y, ...initial_point);
      currentPath = [];
      path.push(currentPath);
    }
  }

  if (image) {
    return (

      <div>
        {image && (
          <div>
            <Sketch setup={setup} draw={draw} mouseReleased={mouseReleased} />
          </div>
        )}
        <ColorSelector selectColor={color => setColor(color)} />
      </div>
    )
  } else {
    return (
      <FileInput selectImage={setImage} />
    );
  }
}
