import React from 'react';
import Sketch from 'react-p5';
import ColorSelector from './ColorSelector';
import FileInput from '../FileInput';
import EraserIcon from './eraser/EraserIcon.png'

var TWO_PI;
var HALF_PI;
var random;
var b;
var img;
var pg;
var imgWidth;
var imgHeight;

var angleSlider;
let distanceSlider;

var blobPoints = [];
let numPoints = 6; // try different values for different shaped blobs
let baseRadius = 100;
let radiusRandomness = 0.2; // amount of random variation in the blob radius 
let cpOffsetAngle;
let cpdist;

export default function P5Mold() {
    const [color, setColor] = React.useState(['#ff0000']);
    const [image, setImage] = React.useState(null);


    const setup = (p5, canvasParentRef) => {
        p5.createCanvas(400, 400).parent(canvasParentRef);
        pg = p5.createGraphics(600, 600);
        img = p5.loadImage(image, img => {
            p5.image(img, 0, 0);
        });

        angleSlider = p5.createSlider(0, 2.4, 2, 0.05);
        angleSlider.position(20, 250);
        angleSlider.changed(buildBlob);

        distanceSlider = p5.createSlider(10, 150, 50, 5);
        distanceSlider.position(20, 250);
        distanceSlider.changed(buildBlob);

        buildBlob();
    }

    const draw = p5 => {

        imgWidth = img.width;
        imgHeight = img.height;

        if (imgWidth > 0 && imgHeight > 0) {
            p5.resizeCanvas(imgWidth, imgHeight);
        }

        p5.image(img, 0, 0);

        pg.fill(color);
        pg.stroke(color);
        pg.beginShape();
        pg.vertex();
        for (b = 1; b < blobPoints.length; b++) {
            // start from 1 (the second node in the ring)
            let bp = blobPoints[b];
            let pp = blobPoints[b - 1]; // previous node
            // bezier points go:
            // second control point from previous node
            // first control point from this node
            // x and y of this node
            pg.bezierVertex(pp.cp[1].x, pp.cp[1].y, bp.cp[0].x, bp.cp[0].y, bp.x, bp.y);

            let lastp = blobPoints[blobPoints.length - 1];
            let firstp = blobPoints[0]

            pg.bezierVertex(lastp.cp[1].x, lastp.cp[1].y, firstp.cp[0].x, firstp.cp[0].y, firstp.x, firstp.y);
            pg.endShape();

        }
        p5.image(pg, 0, 0, imgWidth, imgHeight);
    }

    function buildBlob(p5) { // this creates a new blob with current settings
        blobPoints = []; // empty the array

        // get values from the sliders
        cpOffsetAngle = angleSlider.value();
        cpdist = distanceSlider.value();

        // generate points around a ring
        for (let p = 0; p < numPoints; p++) {
            let a = p * TWO_PI / numPoints; // angle of this point
            let r = baseRadius + p5.random(-radiusRandomness * baseRadius, radiusRandomness * baseRadius); // radius randomiser
            // create an object storing the x and y coordinate, and the angle
            // as well as an empty array for storing the control points
            let bp = {
                x: p5.cos(a) * r,
                y: p5.sin(a) * r,
                angle: a,
                cp: []
            };
            blobPoints.push(bp);
        }

        for (let b = 0; b < blobPoints.length; b++) { // run through the ring
            let thisp = blobPoints[b]; // current node
            let randomangle = random(-cpOffsetAngle, cpOffsetAngle); // random angle for control points

            let cp1angle = thisp.angle - (HALF_PI + randomangle);
            let cp2angle = thisp.angle + (HALF_PI - randomangle);
            // make sure the two angles of the control points add up to 180 degrees
            // to keep them in the same line and create a smooth join

            // create the control points
            // note that we use cos and sin to create coordinates
            // relative to the node point
            let cp1 = {
                x: thisp.x + (p5.cos(cp1angle) * cpdist),
                y: thisp.y + (p5.sin(cp1angle) * cpdist)
            };
            let cp2 = {
                x: thisp.x + (p5.cos(cp2angle) * cpdist),
                y: thisp.y + (p5.sin(cp2angle) * cpdist)
            };

            thisp.cp = [cp1, cp2]; // store control points in the current node in the blobPoints array

        }
    }


    if (image) {

        return (
            <div>
                {image && (
                    <div>
                        <Sketch setup={setup} draw={draw} />
                    </div>
                )}
                <ColorSelector selectColor={color => setColor(color)} />
            </div>
        )

    } else {
        return (
            <FileInput selectImage={setImage} />
        );
    }
}
