import React from 'react-p5'; 
import Sketch from 'react-p5'; 


let c = "black"; 

export default function ColorSelector(props){

const setup = (p5, canvasParentRef) => {
p5.createCanvas(100,100)
p5.background(205)
}

const draw = p5 => {
        p5.noStroke();
        p5.fill("red");
        p5.ellipse(p5.width/2, p5.height/2, 40);
        props.c = "red";

        p5.noStroke(); 
        p5.fill("blue");
        p5.ellipse(p5.width/2, p5.height/2, 40);
        props.c="blue"; 
}

return <Sketch setup={setup} draw ={draw} />
}
