import Sketch from 'react-p5';

let x, y, px, py; 
let ix =[], iy=[]; 

export default function Vector(){

    const setup = (p5, canvasParentRef) => {
        p5.createCanvas(600,600).parent(canvasParentRef);

    }

    const draw = p5 => {
        p5.background(225);
            const mouseClicked = p5 => {
        ix = p5.mouseX;
        iy = p5.mouseY;
        console.log(ix, iy); 

        p5.stroke('red'); 
        p5.ellipse(p5.mouseX, p5.mouseY, 10);
        return(ix,iy); 
    }
    return(p5.mouseClicked); 
    }   



    const mouseDragged = p5 => {
        x = p5.mouseX;
        y = p5.mouseY; 
        px = p5.pmouseX;
        py = p5.pmouseY;

        p5.strokeWeight(5);
        p5.stroke(0);
        p5.line(px, py, x, y);
        console.log('line'); 
    }

     const mouseReleased = p5 => {
          p5.line(px, py, ix, iy);
          console.log('close'); 
    }

    return <Sketch 
    setup={setup} 
    draw={draw}
    mouseClicked={mouseClicked}
    mouseDragged={mouseDragged}  
    mouseReleased={mouseReleased}
    />
}
