import React from 'react'; 

import Sketch from 'react-p5';
import Sample from './sample/Sample.jpg';

let penSize = 5;
let penState = 0;

let init; 
var x, y, px, py;
var path = []; 

let colorPicker;


let img; 
let pg; 

export default function Vector(){
const [setColor, drawLasso] = useState(false); 

    const preload = p5 => {
    img = p5.loadImage(`${Sample}`);
    }

    const setup = (p5, canvasParentRef) => {
        p5.createCanvas(600,600).parent(canvasParentRef);
        pg = p5.createGraphics(600, 600); 
        colorPicker = p5.createColorPicker('#ed225d');
        colorPicker.position(p5.width/3, p5.height-20);         
    }

    const draw = p5 => {

        noFill();

        x = p5.mouseX;
        y = p5.mouseY;
        px = p5.pmouseX;
        py = p5.pmouseY;

        p5.image(img, 0, 0);
        pg.strokeWeight(penSize); 

            if (p5.mouseIsPressed){
                    if (penState === 0){
                    pg.stroke(colorPicker.color()); 
                    const point = { x: mouseX, y: mouseY }; 
                    path.push(point); 
                    init = path[0]; 
                    console.log(init);}
            }
            beginShape();
            path.forEach(point => {
                vertex(point.x, point.y);
            }); 
            endShape();
            p5.image(pg, 0, 0);
    }

    const mouseReleased = () => { 
        pg.line(x, y, ...init); 
    }

     return <Sketch preload={preload} setup={setup} draw={draw} mouseReleased={mouseReleased}/>  
    }
