import Sketch from 'react-p5';


var x = 0,
    y = 0,
    px = 0,
    py = 0,
    easing = .9;

export default function Vector(){

    const setup = (p5, canvasParentRef) => {
        p5.createCanvas(600,600).parent(canvasParentRef);
        p5.background(225);  
    }

    const draw = p5 => {
        x = p5.mouseX;
        y = p5.mouseY;
        px = p5.mouseX;
        py = p5.mouseY;
        return false; 
    }

    const mouseDragged = p5 => {
         var targetX = p5.mouseX,
      targetY = p5.mouseY;
  x += (targetX - x) * easing;
  y += (targetY - y) * easing;
  p5.strokeWeight(5);
  p5.line(x,y,px,py);
  px = x;
  py = y; 
    return false;
    }

    const mouseReleased = p5 => {
        p5.strokeWeight(0);
        p5.ellipse(p5.mouseX, p5.mouseY, 15);  
    }

    return <Sketch 
    setup={setup}  
    draw={draw} 
    mouseDragged={mouseDragged} 
    mouseReleased={mouseReleased} />
}
