import Sketch from 'react-p5';
import Sample from './sample/Sample.jpg';

let init; 
var x, y, px, py;
var coords = []; 

export default function Vector(){

    const preload = p5 => {
    img = p5.loadImage(`${Sample}`);
    }

    const setup = (p5, canvasParentRef) => {
        p5.createCanvas(600,600).parent(canvasParentRef);
        pg = p5.createGraphics(600, 600); 
        colorPicker = p5.createColorPicker('#ed225d');
        colorPicker.position(0, height + 5);

    }

    const draw = p5 => {
        x = p5.mouseX;
        y = p5.mouseY;
        px = p5.pmouseX;
        py = p5.pmouseY;

        if (p5.mouseIsPressed){
            p5.strokeWeight(10); 
            p5.stroke('red'); 
            p5.line(x, y, px, py); 
            coords.push([x, y]); 
            init = coords[0]; 
            console.log(init);
        }
    }

    const mouseReleased = p5 => { 
        p5.line(x, y, ...init); 
    }

     return <Sketch preload={preload} setup={setup} draw={draw} mouseReleased={mouseReleased}/>  
    }
