import Sketch from 'react-p5';

var x, y, px, py;
var coords = []; 

export default function Vector(){

    const setup = (p5, canvasParentRef) => {
        p5.createCanvas(600,600).parent(canvasParentRef);
              p5.background(225);       
    }

    const draw = p5 => {
        x = p5.mouseX;
        y = p5.mouseY;
        px = p5.pmouseX;
        py = p5.pmouseY;

        if (p5.mouseIsPressed){
            p5.strokeWeight(10); 
            p5.stroke('red'); 
            p5.line(x, y, px, py); 
            coords.push([x, y]); 
            console.log(coords); 
        }
    }

    const coordsList = coords.map((item, index) => (
        <list>{coords}</list>
    )); 

    const mouseReleased = p5 => {
        p5.stroke('none');
        p5.fill('white');  
        p5.ellipse(coordsList[0], 10); 
    }
     return <Sketch setup={setup} draw={draw} mouseReleased={mouseReleased}/>  
    }
