import Sketch from 'react-p5';

let x, y, px, py; 
let ix =[], iy=[]; 

export default function Vector(){

    const setup = (p5, canvasParentRef) => {
        p5.createCanvas(600,600).parent(canvasParentRef);
          p5.background(225);
    }

    const draw = p5 => {
        ix = p5.mouseX;
        iy = p5.mouseY;

        if (p5.mouseIsPressed){
            console.log(ix,iy); 
        } return ( ix, iy);  
        }


 const mouseClicked = p5 => {
        console.log(ix, iy); 
        p5.stroke('red'); 
        p5.ellipse(ix, iy, 10); 
    }

const mouseDragged = p5 => {
         x = p5.mouseX;
         y = p5.mouseY; 
         px = p5.pmouseX;
         py = p5.pmouseY;
         p5.line( x, y, px, py);
         console.log('line'); 
         return(px, py, x, y);         
    } 

    const mouseReleased = p5 => {
        p5.line(px, py, ix,iy)
        console.log('close');
    }

    return <Sketch 
    setup={setup} 
    draw={draw}
    //mouseMoved={mouseMoved}
    mouseClicked={mouseClicked}
    mouseDragged={mouseDragged}  
    mouseReleased={mouseReleased}
    />
}
