import Sketch from 'react-p5';

let x, y, px, py; 
let ix =[], iy=[]; 

export default function Vector(){

    const setup = (p5, canvasParentRef) => {
        p5.createCanvas(600,600).parent(canvasParentRef);
        p5.background(225);  
    }

    const mouseClicked = p5 => {
        ix = p5.mouseX;
        iy = p5.mouseY;
        p5.stroke('red'); 
        p5.ellipse(ix, iy, 10);
        console.log(ix, iy); 
        return(ix,iy); 
    }

    const mouseDragged = p5 => {
        x = p5.mouseX;
        y = p5.mouseY; 
        px = p5.pmouseX;
        py = p5.pmouseY;

        p5.strokeWeight(5);
        p5.stroke(0);
        p5.line(px, py, x, y);
        console.log('line'); 
    }

     const mouseReleased = p5 => {
          p5.line(px, py, ix, iy);
          console.log('close'); 
    }

    return <Sketch 
    setup={setup}  
    mouseClicked={mouseClicked}
    mouseDragged={mouseDragged}  
    mouseReleased={mouseReleased}
    />
}
