import { Point } from './helpers';
export declare enum pathActions {
    ADD = "ADD",
    DELETE = "DELETE",
    MODIFY = "MODIFY",
    MOVE = "MOVE",
    RESET = "RESET",
    CHANGE = "CHANGE"
}
export declare type pathReducerAction = {
    type: pathActions.ADD;
    payload: Point;
} | {
    type: pathActions.DELETE;
    payload: number;
} | {
    type: pathActions.MODIFY;
    payload: {
        index: number;
    } & Point;
} | {
    type: pathActions.MOVE;
    payload: Point;
} | {
    type: pathActions.RESET;
} | {
    type: pathActions.CHANGE;
    payload: Point[];
};
export interface PathState {
    points: Point[];
    closed: boolean;
}
export declare function pathReducer(state: PathState, action: pathReducerAction): [PathState, boolean];
